const agent = require('superagent')

const receiveToken = ({clientId, clientSecret, url, scope}) =>
    agent
        .post(url)
        .accept('json')
        .type('form')
        .retry(3)
        .send({
            client_id: clientId,
            grant_type: 'client_credentials',
            client_secret: clientSecret,
            scope
        })

module.exports = {receiveToken}
