const params = require('../../../src/processors/params')

describe('params', () => {
    it("looks at the config, if 'inConfig' is set", () => {
        params.getOption({test: true}, {inConfig: 'test'}).should.equal(true)
    })

    it("grabs deep values from the config, if 'inConfig' is set", () => {
        params.getOption({deep: {values: true}}, {inConfig: 'deep.values'}).should.equal(true)
    })

    it("uses the value when 'inConfig' is not set", () => {
        params.getOption({}, 'test').should.equal('test')
    })
})
