const _ = require('underscore');

function validateAppRoleVaultInfo(vaultUrl, credentials) {
	let error = null;
	if(_(vaultUrl).isEmpty()){
		error = 'Missing vault url!'
	} else if (_(credentials).isEmpty()) {
		error = 'Missing credentials object (should have role_id and secret_id)!'
	} else if (_(credentials.role_id).isEmpty()) {
		error = 'Missing credentials.role_id!'
	} else if (_(credentials.secret_id).isEmpty()) {
		error = 'Missing credentials.secret_id!'
	}
	return error;
}

module.exports = validateAppRoleVaultInfo;