. ./.fg-env
chmod 777 ./tags.json
chmod 777 ./app_parameters.json

# Make sure the fargate info is there
echo Account Id:  $FARGATE_ACCOUNT_ID

# We need to unset the default AWS env variables, or the fargate commands won't use box's role
unset AWS_ACCESS_KEY_ID
unset AWS_SECRET_ACCESS_KEY

aws s3 cp s3://fargate-deploy/v3/v3.1/fg-deploy/fg-deploy.sh .
chmod 755 ./fg-deploy.sh
./fg-deploy.sh || exit 1
