const injectVaultIntoCloudFoundryServices = require('./injectVaultIntoCloudFoundryServices')
const services = require('@monsantoit/cloud-foundry').services;

function getVaultResolvedCloudFoundryServices(vaultUrl, credentials) {
	return injectVaultIntoCloudFoundryServices(vaultUrl, credentials, services).then(() => {
		return services;
	});
}

module.exports = getVaultResolvedCloudFoundryServices;